import { Component, OnInit } from '@angular/core';
import { ConnectionService, Message } from '../connection.service';
import { PlayerModel } from '../player/player-model';
import { Scoreboard } from '../scoreboard/scoreboard.component';
import { CardUtils } from '../cards/card-utils';

@Component({
  selector: 'app-dashboard',
  templateUrl: './gameboard.component.html',
  styleUrls: [ './gameboard.component.css' ]
})
export class GameboardComponent {

  names: string[] = ['John','Eric','Nancy','Allen','Gavin']
  myHand: string[] = []
  myName: string = "XXX"
  predictions: number[] = []
  trick: number = 0
  currentTrick: string[] = []
  scoreboard: Scoreboard = {
    players: [],
    scores: [],
    hidden: true
  };
  predictionRequested: boolean = false
  predictionRange: number[] = []
  myPrediction: number = -1
  playCardRequested: boolean = false
  trumps: string = ""

  players: PlayerModel[] = []

  constructor(
    public connection: ConnectionService
  ) {
    console.log("gameboard constructor")

    this.connection.getMessageBehaviourSubject().subscribe({
      next: (message:Message) => {
        switch(message.messageType) {
          case "hand": {
            this.myHand = message.data.hand
            this.players.forEach(p => {
              p.clearDealer()
              p.setTricksWon(0)
            })
            this.players[message.data.dealer].setDealer()
            let trumpSuit = CardUtils.getSuit(message.data.trumps)
            this.trumps = CardUtils.getSuitSymbol(trumpSuit)
            break
          }
          case "predictionRequest": {
            this.myPrediction = -1
            this.predictionRange = []
            for (let i = 0; i <= this.myHand.length ; i++) {
              this.predictionRange.push(i);
           }
            this.predictionRequested = true
            break
          }
          case "predictions": {
            this.players[message.data.prediction.playerId].setCurrentPrediction(message.data.prediction.prediction)
            break
          }
          case "playCard": {
            this.playCardRequested = true
            break
          }
          case "cardPlayed": {
            this.currentTrick.push(message.data.card)
            break
          }
          case "trickResult": {
            setTimeout( () => {
              this.currentTrick = []
            }, 5000)
            let t = this.players[message.data.trickResult.winningPlayer].getTricksWon()
            this.players[message.data.trickResult.winningPlayer].setTricksWon(t + 1)
            break
          }
          case "newPlayer": {
            this.addPlayer(message)
            break
          }
          case "scoreboard": {
            this.scoreboard.players = this.players
            this.scoreboard.scores = message.data.scoreboard
            this.scoreboard.hidden = false
            break
          }
          default: {
            console.log("dashboard: ignoring message")
          }
        }
      }
    })
  }

  addPlayer(message: Message) {
    let p = new PlayerModel()
    p.setName(message.data.name)
    this.players.push(p)
  }

  playerSelected() {
    this.connection.send("register",{"name":this.myName});
  }

  predictionSelected() {
    this.connection.send("prediction",{"prediction":this.myPrediction});
    this.predictionRequested = false
  }

  cardSelected(card: string) {
    if (this.playCardRequested) {
      this.connection.send("card",{"card":card});
      this.playCardRequested = false
      let index = this.myHand.findIndex(c => c === card)
      this.myHand.splice(index,1)
    }
  }

  newGame() {
    console.log("New game")
    this.connection.send("newGame")
  }
}

<div class="main-container">

  <div class="title">Clag</div>

  <div class="lhs-panel">
    <div class="connection-status">
      <p *ngIf="connection.isConnected()">Connection: Up</p>
      <p *ngIf="!connection.isConnected()">Connection: Down</p>
      <button (click)="newGame()">New Game</button>
    </div>

    <div class="player-selection">
      <select [(ngModel)]="myName" (change)="playerSelected()">
        <option *ngFor="let name of names" [ngValue]="name">{{name}}</option>
      </select>
    </div>

    <div class="my-prediction" *ngIf="predictionRequested">
      <p>Make prediction!...</p>
      <select [(ngModel)]="myPrediction" (change)="predictionSelected()">
        <option *ngFor="let p of predictionRange" [ngValue]="p">{{p}}</option>
      </select>
    </div>
  </div>

  <div class="game-board">

    <div class="playing-area">

      <div class="players">
        <ng-template ngFor let-player let-i="index" [ngForOf]="players">
          <component-player class="player-container player-{{i}}" [player]="player"></component-player>
        </ng-template>
      </div>

      <div class="current-trick">
        <ng-template ngFor let-card [ngForOf]="currentTrick">
          <component-card class="myhand-card" [value]="card" [hidden]="false" [selected]="false" [playable]="true"></component-card>
        </ng-template>      
      </div>

    </div>


    <div class="my-hand">
      <div class="trumps"><div>Trumps</div><div>{{trumps}}</div></div>
      <div>
        <ng-template ngFor let-card [ngForOf]="myHand">
          <component-card (click)="cardSelected(card)" class="myhand-card" [value]="card" [hidden]="false" [selected]="false" [playable]="true"></component-card>
        </ng-template>
      </div>
    </div>

    <div class="play-card" *ngIf="playCardRequested">
      <p>Your turn!...</p>
    </div>
  </div>

  <div class="scoreboard">
    <component-scoreboard [scoreboard]="scoreboard"></component-scoreboard>
  </div>

</div>
