import { Injectable } from '@angular/core';

@Injectable({ providedIn: 'root' })
export class ConnectionService {

  names: string[] = ['John','Eric','Nancy','Allen','Gavin']
  title = 'default';
  myPlayerId: number = -1
  myConnectionId: number = -1
  myHand: number[] = []
  myName: string = "XXX"
  predictions: number[] = []
  ws: WebSocket
  trick: number = 0
  scoreboard: any[][] = []

  constructor() {

    this.ws = new WebSocket("ws://localhost:8080");

      this.ws.onopen = function() {

        // Web Socket is connected, send data using send()
        console.log("Connection successful")
      };

      this.ws.onmessage = (evt) => { 
        let received_msg = JSON.parse(evt.data);
        console.log("Message is received... " + JSON.stringify(received_msg));

        switch(received_msg.messageType) {
          case "connection": {
            this.myConnectionId = received_msg.connectionId
            break
          }
          case "newPlayer": {
            if ( this.myConnectionId === received_msg.connectionId) {
              this.myPlayerId = received_msg.playerId
            }
            console.log(`New player: ${received_msg.data.name}`)
            break
          }
          case "predictionRequest": {
            this.ws.send(`{"connectionId": ${this.myConnectionId},"playerId": ${this.myPlayerId}, "messageType":"prediction","data":{"prediction":1}}`)
            break
          }
          case "hand": {
            this.myHand = received_msg.data.hand
            this.trick = 0
            break
          }
          case "predictions": {
            this.predictions = received_msg.data.predictions
            break
          }
          case "playCard": {
            this.sendCard()
            break
          }
          case "scoreboard": {
            this.scoreboard = received_msg.data
            break
          }
          default: {
            console.log("unrecognised message type received")
          }
        }

      };

      this.ws.onclose = () => { 

        // websocket is closed.
        this.myConnectionId = -1
        alert("Connection is closed..."); 
      };
  }

  send(messageType: string, data?: any) {
    let dataSection: string = JSON.stringify(data) ? `,"data":{"${data}"}` : ""
    this.ws.send(`{"connectionId": ${this.myConnectionId},"playerId": ${this.myPlayerId},"messageType":"${messageType}"${dataSection}}`);
  }

  sendCard() {

  }

  isConnected(): boolean {
    return this.myConnectionId !== -1
  }

}
