import { Component, OnInit } from '@angular/core';
import { ConnectionService, Message } from '../connection.service';
import { PlayerModel } from '../player/player-model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './gameboard.component.html',
  styleUrls: [ './gameboard.component.css' ]
})
export class GameboardComponent {

  names: string[] = ['John','Eric','Nancy','Allen','Gavin']
  myHand: string[] = []
  myName: string = "XXX"
  predictions: number[] = []
  trick: number = 0
  scoreboard: any[][] = []
  predictionRequested: boolean = false
  predictionRange: number[] = []
  myPrediction: number = -1
  playCardRequested: boolean = false
  myCardSelection: string = "XX"

  players: PlayerModel[] = []

  constructor(
    public connection: ConnectionService
  ) {
    console.log("dashboard constructor")

    this.connection.getMessageBehaviourSubject().subscribe({
      next: (message:Message) => {
        switch(message.messageType) {
          case "hand": {
            this.myHand = message.data.hand
            break
          }
          case "predictionRequest": {
            this.predictionRange = []
            for (let i = 0; i <= this.myHand.length ; i++) {
              this.predictionRange.push(i);
           }
            this.predictionRequested = true
            break
          }
          case "predictions": {
            this.predictions = message.data.predictions
            break
          }
          case "playCard": {
            this.playCardRequested = true
            break
          }
          case "newPlayer": {
            this.addPlayer(message)
            break
          }
          case "scoreboard": {
            this.scoreboard = message.data.scoreboard
            break
          }
          default: {
            console.log("dashboard: ignoring message")
          }
        }
      }
    })
  }

  addPlayer(message: Message) {
    let p = new PlayerModel()
    p.setName(message.data.name)
    this.players.push(p)
  }

  playerSelected() {
    this.connection.send("register",{"name":this.myName});
  }

  predictionSelected() {
    this.connection.send("prediction",{"prediction":this.myPrediction});
    this.predictionRequested = false
  }

  cardSelected() {
    this.connection.send("card",{"card":this.myCardSelection});
    this.playCardRequested = false
  }

  newGame() {
    console.log("New game")
    this.connection.send("newGame")
  }
}

<div class="main-container">

  <div class="title">Clag</div>

  <div class="lhs-panel">
    <div class="connection-status">
      <p *ngIf="connection.isConnected()">Connection: Up</p>
      <p *ngIf="!connection.isConnected()">Connection: Down</p>
      <button (click)="newGame()">New Game</button>
    </div>

    <div class="player-selection">
      <select [(ngModel)]="myName" (change)="playerSelected()">
        <option *ngFor="let name of names" [ngValue]="name">{{name}}</option>
      </select>
    </div>

    <div class="my-prediction" *ngIf="predictionRequested">
      <p>Make prediction!...</p>
      <select [(ngModel)]="myPrediction" (change)="predictionSelected()">
        <option *ngFor="let p of predictionRange" [ngValue]="p">{{p}}</option>
      </select>
    </div>
  </div>

  <div class="game-board">

    <div class="players">
      <ng-template ngFor let-player let-i="index" [ngForOf]="players">
        <component-player class="player-{{i}}" [player]="player"></component-player>
      </ng-template>
    </div>

    <div class="my-hand">
      <div>
        <ng-template ngFor let-card [ngForOf]="myHand">
          <component-card class="myhand-card" [value]="card" [hidden]="false" [selected]="false" [playable]="true"></component-card>
        </ng-template>
      </div>
    </div>

    <div class="play-card" *ngIf="playCardRequested">
      <p>Play card!...</p>
      <select [(ngModel)]="myCardSelection" (change)="cardSelected()">
        <option *ngFor="let c of myHand" [ngValue]="c">{{c}}</option>
      </select>
    </div>
  </div>

  <div class="scores">
    <div>Scores:</div>
    <div *ngFor="let playerScores of scoreboard">
      <div style="display: inline-block" *ngFor="let score of playerScores">
        {{score.prediction}} {{score.made}} {{score.score}}
      </div>
    </div>
  </div>

</div>
